var mqttSplit = msg.payload.split('/');
var trayProps = {
    box         :   mqttSplit[0],
    type        :   mqttSplit[1],
};
flow.set('trayProps',trayProps);
flow.set('settingInProgress', false);
flow.set('traysConfig',[]);
return {topic: "getTrayProps", payload: true};

if (flow.get('traysConfig').length > 0) return [null,null];
let trayProps  = flow.get('trayProps');
node.warn("Asking for tray from database");
var newMsg =
{
    topic: trayProps.box + '/' + trayProps.type + '/' + 'ALL' + '/echo',
    payload: JSON.stringify({ topic: trayProps.box + '/' + trayProps.type + '/' + 'ALL' + '/setting/echo' })
};
return [newMsg, { topic: "getTrayProps", payload: true }];

let mqttTopic = msg.topic.split('/')
let trayName = mqttTopic[2];
let actionTopic = mqttTopic[4];
if (trayName == env.get("ADMINNAME"))
{
    if (actionTopic == 'setting') actionTopic = 'reloadTrays'
    return {topic:actionTopic, payload:msg.payload};
}
if (actionTopic == 'echo')
{
    return {topic:'echo', payload:msg.payload};
}
let traysConfig = flow.get('traysConfig');
let trayConfig = null;
for (let ii = 0; ii < traysConfig.length; ++ii)
{
    if (traysConfig[ii].tray.name == trayName)  trayConfig = traysConfig[ii];
}
if (trayConfig ==  null) return null;
return {topic:actionTopic, payload:msg.payload, trayConfig:trayConfig};

let tray = msg.payload;
if (tray.name == env.get("ADMINNAME"))
{
    tray.reloadTrays.value = 0;
    flow.set('adminTray', tray);
    return null;
}
let blinkybusCubes = [];
let blinkybusSettingCubes = [];
let keys = Object.keys(msg.payload)
for (let ikey  in keys)
{
    if (msg.payload[keys[ikey]].hasOwnProperty('type'))
    {
        if (msg.payload[keys[ikey]].hasOwnProperty('blinkybus')) 
        {
            blinkybusCubes.push(keys[ikey]);
            if (msg.payload[keys[ikey]].action == "setting") 
            {
                blinkybusSettingCubes.push(keys[ikey]);
            }
        }
    }
}
flow.get('traysConfig').push({tray:tray, blinkybusCubes:blinkybusCubes, blinkybusSettingCubes:blinkybusSettingCubes, archiveTime:tray.timeStamp});
//node.warn(flow.get('traysConfig'));
return null;

let bufsize = 4;
let buffer = new ArrayBuffer(bufsize);
let settingsView = new DataView(buffer);
settingsView.setUint8(0, 1); //setting
settingsView.setUint8(1, msg.payload.address); //address
settingsView.setInt16(2, msg.payload.value, true); //value
return {topic:msg.topic, payload:Buffer.from(buffer),rate:500};

let topicSplit = msg.topic.split('/');
let trayName = topicSplit[1];
let traysConfig = flow.get('traysConfig');
if (traysConfig == undefined) return [null,null,null];
if (traysConfig.length < 1) return [null,null,null];
let trayConfig = null;
let trayIndex = -1;
for (let ii = 0; ii < traysConfig.length; ++ii)
{
    if (traysConfig[ii].tray.name == trayName)
    {
        trayConfig = traysConfig[ii];
        trayIndex = ii;
    }
}
if (trayConfig ==  null) return null;

trayConfig.tray.timeStamp = new Date().getTime();
let stateView  = new DataView(new ArrayBuffer(2));
for (let ib = 0; ib < 2; ++ib)   stateView.setUint8(ib, msg.payload[1 - ib]);
let state = stateView.getInt16(0)
if (state == 1)
{
    return [null, null, {topic:"initDevice",payload:trayConfig}];
}

for (let icube = 0; icube < trayConfig.blinkybusCubes.length; ++icube)
{
    let cube = trayConfig.tray[trayConfig.blinkybusCubes[icube]];
    let dataView  = new DataView(new ArrayBuffer(2));
    for (let ib = 0; ib < 2; ++ib)   dataView.setUint8(ib, msg.payload[(cube.blinkybus.address * 2) + 1 - ib]);
    cube.value = dataView.getInt16(0);
    cube.value = cube.value / cube.blinkybus.scale;
}
trayConfig.tray.timeStamp = new Date().getTime();
let newMsg = {topic:trayName, payload:trayConfig.tray};
let archiveMsg = null;
let archiveTimes = flow.get('archiveTimes');
if ((trayConfig.tray.timeStamp - trayConfig.archiveTime) > trayConfig.tray.arcPeriod)
{
    trayConfig.archiveTime = trayConfig.tray.timeStamp;
    archiveMsg = newMsg;
}
return  [newMsg,archiveMsg,null];

let trayConfig = msg.payload;
let msgs = [];
msgs.push({"address":0,"value":0});
for (let icube = 0; icube < trayConfig.blinkybusSettingCubes.length; ++icube)
{
    let cube = trayConfig.tray[trayConfig.blinkybusSettingCubes[icube]];
    let cubeValue = Math.round(cube.value * cube.blinkybus.scale);
    msgs.push({"address":cube.blinkybus.address,"value":cubeValue });
}
let publishTopic = trayConfig.tray.type + "/" + trayConfig.tray.name + "/setting";
return {topic : publishTopic, payload: msgs};

let tray = msg.payload;
let newMsg = 
{
    topic: flow.get('trayProps').box + '/' + tray.type + '/' + tray.name + '/reading',
    payload: JSON.stringify(tray)
};
return  newMsg;

let tray = msg.payload;
let newMsg = 
{
    topic: flow.get('trayProps').box + '/' + tray.type + '/' + tray.name + '/archive',
    payload: JSON.stringify(tray)
};
return  newMsg;

